import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { client } from "../api/graphqlClient";
import { 
  CREAR_COMPROBADOR_ADMIN_MUTATION, 
  CREATE_USUARIO_MUTATION,
  GET_USUARIOS,
  EDITAR_USUARIO_ADMIN_MUTATION
} from "../api/adminQueries";
import { FiUser, FiMail, FiPhone, FiLock, FiUserPlus, FiPlus, FiX } from "react-icons/fi";

const FormField = ({ label, children, icon: Icon, required = false }) => (
  <div className="space-y-1">
    <label className="flex items-center text-sm font-medium text-gray-700">
      {Icon && <Icon className="mr-2 text-orange-600" size={16} />}
      {label}
      {required && <span className="text-red-500 ml-1">*</span>}
    </label>
    {children}
  </div>
);

export default function AdminForms() {
  // Estados para usuario/comprobador
  const [uNombre, setUNombre] = useState("");
  const [uApellido, setUApellido] = useState("");
  const [uCorreo, setUCorreo] = useState("");
  const [uPassword, setUPassword] = useState("");
  const [uTelefono, setUTelefono] = useState("");

  // Estados para comprobador
  const [cCI, setCCI] = useState("");
  const [cNombreUsuario, setCNombreUsuario] = useState("");
  const [cCupos, setCCupos] = useState(0);
  
  // Estados generales
  const [msg, setMsg] = useState({ type: '', text: '' });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // Estado para asignar rol productor a usuario existente
  const [prodUsuarioId, setProdUsuarioId] = useState("");
  const [assigningProductor, setAssigningProductor] = useState(false);

  // Estado para crear comprobador desde usuario existente por correo
  const [compCorreo, setCompCorreo] = useState("");
  const [compCi, setCompCi] = useState("");
  const [compCuposExistente, setCompCuposExistente] = useState("");
  const [creatingExistingComprobador, setCreatingExistingComprobador] = useState(false);


  const resetForm = (formType) => {
    if (formType === 'comprobador') {
      setUNombre('');
      setUApellido('');
      setUCorreo('');
      setUPassword('');
      setUTelefono('');
      setCCI('');
      setCNombreUsuario('');
      setCCupos(0);
    }
  };

  const handleAsignarProductor = async (e) => {
    e.preventDefault();
    setMsg({ type: "", text: "" });
    if (!prodUsuarioId.trim()) {
      showMessage("error", "Debes ingresar un ID de usuario");
      return;
    }

    try {
      setAssigningProductor(true);

      // Obtener usuarios para encontrar roles actuales
      const data = await client.request(GET_USUARIOS);
      const usuarios = data.usuarios || [];
      const usuario = usuarios.find(u => u.id === prodUsuarioId.trim());

      if (!usuario) {
        throw new Error("No se encontró un usuario con ese ID");
      }

      const rolesActuales = Array.isArray(usuario.roles) ? usuario.roles : (usuario.roles ? [usuario.roles] : []);
      const rolesNuevos = rolesActuales.includes("productor")
        ? rolesActuales
        : [...rolesActuales, "productor"]; 

      await client.request(EDITAR_USUARIO_ADMIN_MUTATION, {
        usuarioId: usuario.id,
        nombre: null,
        apellido: null,
        correo: null,
        telefono: null,
        roles: rolesNuevos
      });

      showMessage("success", `Rol productor asignado al usuario ${usuario.correo || usuario.id}`);
      setProdUsuarioId("");
    } catch (err) {
      console.error("Error asignando rol productor:", err);
      const errorMsg = err?.response?.errors?.[0]?.message || err.message || "Error al asignar rol productor";
      showMessage("error", `Error: ${errorMsg}`);
    } finally {
      setAssigningProductor(false);
    }
  };

  const handleCrearComprobadorPorCorreo = async (e) => {
    e.preventDefault();
    setMsg({ type: "", text: "" });

    if (!compCorreo.trim() || !compCi.trim()) {
      showMessage("error", "Debes ingresar correo y CI");
      return;
    }

    try {
      setCreatingExistingComprobador(true);

      const data = await client.request(GET_USUARIOS);
      const usuarios = data.usuarios || [];
      const correoLower = compCorreo.trim().toLowerCase();
      const usuario = usuarios.find(u => (u.correo || "").toLowerCase() === correoLower);

      if (!usuario) {
        throw new Error("No se encontró un usuario con ese correo");
      }

      const cupos = compCuposExistente === "" ? null : Number(compCuposExistente);

      await client.request(CREAR_COMPROBADOR_ADMIN_MUTATION, {
        usuarioId: usuario.id,
        ci: compCi.trim(),
        nombreUsuario: null,
        cuposDisponibles: cupos,
      });

      showMessage("success", `Comprobador creado para ${usuario.correo}`);
      setCompCorreo("");
      setCompCi("");
      setCompCuposExistente("");
      setTimeout(() => navigate("/comprobadores"), 1200);
    } catch (err) {
      console.error("Error creando comprobador por correo:", err);
      const errorMsg = err?.response?.errors?.[0]?.message || err.message || "Error al crear comprobador";
      showMessage("error", `Error: ${errorMsg}`);
    } finally {
      setCreatingExistingComprobador(false);
    }
  };

  const showMessage = (type, text) => {
    setMsg({ type, text });
    setTimeout(() => setMsg({ type: '', text: '' }), 5000);
  };

  const handleCreateComprobador = async (e) => {
    e.preventDefault();
    setMsg({ type: '', text: '' });
    setLoading(true);
    
    try {
      if (!uPassword || uPassword.length < 6) {
        throw new Error("La contraseña debe tener al menos 6 caracteres.");
      }
      
      const userVariables = {
        input: {
          nombre: uNombre,
          apellido: uApellido,
          correo: uCorreo,
          password: uPassword,
          telefono: uTelefono,
        },
      };

      const userRes = await client.request(CREATE_USUARIO_MUTATION, userVariables);
      const createdUser = userRes?.crearUsuario;
      if (!createdUser || !createdUser.id) {
        throw new Error("No se pudo crear el usuario");
      }

      const compVariables = {
        usuarioId: createdUser.id,
        ci: cCI,
        nombreUsuario: cNombreUsuario || `${uNombre} ${uApellido}`.trim(),
        cuposDisponibles: Number(cCupos) || 0,
      };

      await client.request(CREAR_COMPROBADOR_ADMIN_MUTATION, compVariables);
      
      showMessage('success', 'Usuario y comprobador creados correctamente');
      resetForm('comprobador');
      setTimeout(() => navigate("/comprobadores"), 1500);
      
    } catch (err) {
      console.error(err);
      const errorMsg = err?.response?.errors?.[0]?.message || err.message || 'Error al crear el comprobador';
      showMessage('error', `Error: ${errorMsg}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4 md:p-6 max-w-5xl">
      <h2 className="text-2xl md:text-3xl font-bold text-gray-800 mb-6">Formularios Administrativos</h2>

      {/* Mensajes */}
      {msg.text && (
        <div className={`mb-6 p-4 rounded-lg ${msg.type === 'error' ? 'bg-red-100 text-red-700' : 'bg-green-100 text-green-700'}`}>
          {msg.text}
        </div>
      )}

      {/* Crear comprobador para usuario existente por correo */}
      <div className="bg-white rounded-xl shadow-md overflow-hidden transition-all duration-300 mb-8">
        <div className="p-6">
          <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
            <FiUserPlus className="mr-2 text-orange-600" />
            Crear Comprobador desde Usuario existente (correo)
          </h3>

          <form onSubmit={handleCrearComprobadorPorCorreo} className="space-y-4 max-w-md">
            <FormField label="Correo del usuario" icon={FiMail} required>
              <input
                type="email"
                value={compCorreo}
                onChange={(e) => setCompCorreo(e.target.value)}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition"
                placeholder="correo@ejemplo.com"
                required
              />
            </FormField>

            <FormField label="CI" icon={FiUser} required>
              <input
                type="text"
                value={compCi}
                onChange={(e) => setCompCi(e.target.value)}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition"
                placeholder="Número de CI"
                required
              />
            </FormField>

            <FormField label="Cupos disponibles (opcional)" icon={FiUser}>
              <input
                type="number"
                min="0"
                max="10"
                value={compCuposExistente}
                onChange={(e) => setCompCuposExistente(e.target.value)}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition"
                placeholder="Dejar vacío para máximo"
              />
            </FormField>

            <div className="flex justify-end pt-2">
              <button
                type="submit"
                className="flex items-center justify-center px-6 py-2.5 bg-orange-600 text-white rounded-lg hover:bg-orange-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-orange-500 transition disabled:opacity-70"
                disabled={creatingExistingComprobador}
              >
                {creatingExistingComprobador ? (
                  <>
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Creando...
                  </>
                ) : (
                  <>
                    <FiPlus className="mr-2" />
                    Crear Comprobador
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Asignar rol productor a usuario existente */}
      <div className="bg-white rounded-xl shadow-md overflow-hidden transition-all duration-300 mb-8">
        <div className="p-6">
          <h3 className="text-xl font-semibold text-gray-800 mb-4 flex items-center">
            <FiUserPlus className="mr-2 text-orange-600" />
            Asignar rol Productor a Usuario existente
          </h3>

          <form onSubmit={handleAsignarProductor} className="space-y-4 max-w-md">
            <FormField label="ID de Usuario" icon={FiUser} required>
              <input
                type="text"
                value={prodUsuarioId}
                onChange={(e) => setProdUsuarioId(e.target.value)}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition"
                placeholder="ID del usuario al que quieres hacer productor"
                required
              />
            </FormField>

            <div className="flex justify-end pt-2">
              <button
                type="submit"
                className="flex items-center justify-center px-6 py-2.5 bg-orange-600 text-white rounded-lg hover:bg-orange-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-orange-500 transition disabled:opacity-70"
                disabled={assigningProductor}
              >
                {assigningProductor ? (
                  <>
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Asignando...
                  </>
                ) : (
                  <>
                    <FiPlus className="mr-2" />
                    Hacer Productor
                  </>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Formulario de Comprobador */}
      <div className="bg-white rounded-xl shadow-md overflow-hidden transition-all duration-300 mb-8">
        <div className="p-6">
          <h3 className="text-xl font-semibold text-gray-800 mb-6 flex items-center">
            <FiUserPlus className="mr-2 text-orange-600" />
            Crear Nuevo Comprobador
          </h3>
          
          <form onSubmit={handleCreateComprobador} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <FormField label="Nombre" icon={FiUser} required>
                <input
                  type="text"
                  value={uNombre}
                  onChange={(e) => setUNombre(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition"
                  placeholder="Ingrese el nombre"
                  required
                />
              </FormField>
              
              <FormField label="Apellido" icon={FiUser} required>
                <input
                  type="text"
                  value={uApellido}
                  onChange={(e) => setUApellido(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition"
                  placeholder="Ingrese el apellido"
                  required
                />
              </FormField>
              
              <FormField label="Correo Electrónico" icon={FiMail} required>
                <input
                  type="email"
                  value={uCorreo}
                  onChange={(e) => setUCorreo(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition"
                  placeholder="correo@ejemplo.com"
                  required
                />
              </FormField>
              
              <FormField label="Contraseña" icon={FiLock} required>
                <input
                  type="password"
                  value={uPassword}
                  onChange={(e) => setUPassword(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition"
                  placeholder="Mínimo 6 caracteres"
                  minLength="6"
                  required
                />
              </FormField>
              
              <FormField label="Teléfono" icon={FiPhone}>
                <input
                  type="tel"
                  value={uTelefono}
                  onChange={(e) => setUTelefono(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition"
                  placeholder="Ej: 60012345"
                />
              </FormField>
              
              <FormField label="Cédula de Identidad" icon={FiUser} required>
                <input
                  type="text"
                  value={cCI}
                  onChange={(e) => setCCI(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition"
                  placeholder="Número de CI"
                  required
                />
              </FormField>
              
              <FormField label="Nombre de Usuario (opcional)" icon={FiUser}>
                <input
                  type="text"
                  value={cNombreUsuario}
                  onChange={(e) => setCNombreUsuario(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition"
                  placeholder="Dejar en blanco para usar nombre completo"
                />
              </FormField>
              
              <FormField label="Cupos Disponibles" icon={FiUser}>
                <input
                  type="number"
                  min="0"
                  value={cCupos}
                  onChange={(e) => setCCupos(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500 transition"
                  placeholder="Ej: 10"
                />
              </FormField>
            </div>
            
            <div className="flex flex-col sm:flex-row justify-end space-y-3 sm:space-y-0 sm:space-x-4 mt-8">
              <button
                type="button"
                onClick={() => resetForm('comprobador')}
                className="px-6 py-2.5 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-orange-500 transition"
                disabled={loading}
              >
                Limpiar
              </button>
              <button
                type="submit"
                className="flex items-center justify-center px-6 py-2.5 bg-orange-600 text-white rounded-lg hover:bg-orange-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-orange-500 transition disabled:opacity-70"
                disabled={loading}
              >
                {loading ? (
                  <>
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
