import { useEffect, useState } from "react";
import { client } from "../api/graphqlClient";
import { 
  GET_PRODUCTORES, 
  GET_USUARIOS,
  CREAR_PRODUCTOR_ADMIN_MUTATION,
  EDITAR_PRODUCTOR_ADMIN_MUTATION,
  ELIMINAR_PRODUCTOR_ADMIN_MUTATION
} from "../api/adminQueries";

export default function Productores() {
  const [productores, setProductores] = useState([]);
  const [usuarios, setUsuarios] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [editingProductor, setEditingProductor] = useState(null);
  const [formData, setFormData] = useState({
    usuarioId: "",
    nombreUsuario: "",
    direccion: "",
    nit: "",
    telefono: ""
  });

  // Cargar datos
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError("");
        
        // Obtener productores
        const productoresData = await client.request(GET_PRODUCTORES);
        
        // Obtener usuarios para el selector
        const usuariosData = await client.request(GET_USUARIOS);
        
        setProductores(productoresData.productores || []);
        setUsuarios(usuariosData.usuarios || []);
        
      } catch (err) {
        console.error("Error cargando datos:", err);
        setError("Error al cargar los datos");
      } finally {
        setLoading(false);
      }
    };
    
    fetchData();
  }, []);

  // Manejar cambios en el formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Manejar selecci贸n de usuario
  const handleUsuarioChange = (e) => {
    const usuarioId = e.target.value;
    const usuarioSeleccionado = usuarios.find(u => u.id === usuarioId);
    
    setFormData(prev => ({
      ...prev,
      usuarioId,
      nombreUsuario: usuarioSeleccionado ? 
        `${usuarioSeleccionado.nombre || ''} ${usuarioSeleccionado.apellido || ''}`.trim() || 
        usuarioSeleccionado.correo : ""
    }));
  };

  // Enviar formulario (crear o actualizar)
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      if (editingProductor) {
        // Actualizar productor existente
        await client.request(EDITAR_PRODUCTOR_ADMIN_MUTATION, {
          productorId: editingProductor.id,
          ...formData
        });
        
        setProductores(productores.map(p => 
          p.id === editingProductor.id ? { ...p, ...formData } : p
        ));
      } else {
        // Crear nuevo productor
        const { data } = await client.request(CREAR_PRODUCTOR_ADMIN_MUTATION, formData);
        setProductores([...productores, data.crearProductorAdmin]);
      }
      
      setShowModal(false);
      setFormData({
        usuarioId: "",
        nombreUsuario: "",
        direccion: "",
        nit: "",
        telefono: ""
      });
      setEditingProductor(null);
      
    } catch (err) {
      console.error("Error guardando productor:", err);
      setError("Error al guardar el productor. Por favor, int茅ntalo de nuevo.");
    }
  };

  // Iniciar edici贸n
  const startEditing = (productor) => {
    setEditingProductor(productor);
    setFormData({
      usuarioId: productor.usuarioId || "",
      nombreUsuario: productor.nombreUsuario || "",
      direccion: productor.direccion || "",
      nit: productor.nit || "",
      telefono: productor.telefono || ""
    });
    setShowModal(true);
  };

  // Eliminar productor
  const handleDelete = async (productorId) => {
    if (!window.confirm('驴Est谩s seguro de que deseas eliminar este productor? Esta acci贸n no se puede deshacer.')) {
      return;
    }
    
    try {
      await client.request(ELIMINAR_PRODUCTOR_ADMIN_MUTATION, { productorId });
      setProductores(productores.filter(p => p.id !== productorId));
    } catch (err) {
      console.error("Error eliminando productor:", err);
      setError("Error al eliminar el productor. Por favor, int茅ntalo de nuevo.");
    }
  };

  // Cerrar modal y limpiar
  const closeModal = () => {
    setShowModal(false);
    setEditingProductor(null);
    setFormData({
      usuarioId: "",
      nombreUsuario: "",
      direccion: "",
      nit: "",
      telefono: ""
    });
  };

  if (loading) {
    return (
      <div className="p-6 text-center">
        <h1 className="text-3xl font-bold text-orange-600 mb-4">Productores</h1>
        <div className="py-8">Cargando productores...</div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-orange-600">Productores</h1>
        <button
          onClick={() => setShowModal(true)}
          className="bg-orange-600 text-white px-4 py-2 rounded-lg hover:bg-orange-700 transition-colors"
        >
          + Nuevo Productor
        </button>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {productores.length === 0 ? (
        <div className="text-center py-12 bg-white rounded-2xl shadow">
          <p className="text-gray-500 text-lg">No hay productores registrados</p>
        </div>
      ) : (
        <div className="overflow-x-auto bg-white rounded-2xl shadow-xl">
          <table className="w-full">
            <thead className="bg-orange-600 text-white">
              <tr>
                <th className="p-4 text-left">ID</th>
                <th className="p-4 text-left">Usuario</th>
                <th className="p-4 text-left">Direcci贸n</th>
                <th className="p-4 text-left">NIT</th>
                <th className="p-4 text-left">Tel茅fono</th>
                <th className="p-4 text-left">Acciones</th>
              </tr>
            </thead>
            <tbody>
              {productores.map((p) => (
                <tr key={p.id} className="hover:bg-orange-50 transition-colors duration-150 border-b">
                  <td className="p-4 font-mono text-sm">{p.id}</td>
                  <td className="p-4">
                    <div className="font-medium">{p.nombreUsuario}</div>
                    <div className="text-xs text-gray-500">ID: {p.usuarioId}</div>
                  </td>
                  <td className="p-4">{p.direccion || "-"}</td>
                  <td className="p-4">{p.nit || "-"}</td>
                  <td className="p-4">{p.telefono || "-"}</td>
                  <td className="p-4 space-x-2">
                    <button
                      onClick={() => startEditing(p)}
                      className="text-blue-600 hover:text-blue-800 transition-colors"
                      title="Editar"
                    >
                      锔
                    </button>
                    <button
                      onClick={() => handleDelete(p.id)}
                      className="text-red-600 hover:text-red-800 transition-colors ml-2"
                      title="Eliminar"
                    >
                      锔
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Modal de edici贸n/creaci贸n */}
      {showModal && (
        <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl p-6 w-full max-w-md">
            <h2 className="text-xl font-bold text-orange-600 mb-4">
              {editingProductor ? 'Editar Productor' : 'Nuevo Productor'}
            </h2>
            
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Usuario
                </label>
                <select
                  name="usuarioId"
                  value={formData.usuarioId}
                  onChange={handleUsuarioChange}
                  className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500"
                  required
                  disabled={!!editingProductor}
                >
                  <option value="">Selecciona un usuario</option>
                  {usuarios.map(usuario => (
                    <option key={usuario.id} value={usuario.id}>
                      {`${usuario.nombre || ''} ${usuario.apellido || ''}`.trim() || usuario.correo}
                    </option>
                  ))}
                </select>
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Nombre para mostrar
                </label>
                <input
                  type="text"
                  name="nombreUsuario"
                  value={formData.nombreUsuario}
                  onChange={handleChange}
                  className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500"
                  required
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Direcci贸n
                </label>
                <input
                  type="text"
                  name="direccion"
                  value={formData.direccion}
                  onChange={handleChange}
                  className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  NIT
                </label>
                <input
                  type="text"
                  name="nit"
                  value={formData.nit}
                  onChange={handleChange}
                  className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500"
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Tel茅fono
                </label>
                <input
                  type="tel"
                  name="telefono"
                  value={formData.telefono}
                  onChange={handleChange}
                  className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-orange-500"
                />
              </div>
              
              <div className="flex justify-end space-x-3 pt-4 border-t border-gray-200">
                <button
                  type="button"
                  onClick={closeModal}
                  className="px-4 py-2 text-gray-700 hover:bg-gray-100 rounded-lg transition-colors"
                >
                  Cancelar
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-orange-600 text-white rounded-lg hover:bg-orange-700 transition-colors"
                >
                  {editingProductor ? 'Guardar Cambios' : 'Crear Productor'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
