import { useEffect, useState } from "react";
import { client } from "../api/graphqlClient";
import { GET_PRODUCTOS } from "../api/adminQueries";

export default function Productos() {
  const [productos, setProductos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [selectedProducto, setSelectedProducto] = useState(null);

  useEffect(() => {
    const fetchProductos = async () => {
      try {
        setLoading(true);
        setError("");
        const data = await client.request(GET_PRODUCTOS);
        setProductos(data.productos || []);
      } catch (error) {
        console.error("Error fetching productos:", error);
        setError(error.response?.errors?.[0]?.message || "Error al cargar productos");
      } finally {
        setLoading(false);
      }
    };
    fetchProductos();
  }, []);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('es-BO', {
      style: 'currency',
      currency: 'BOB'
    }).format(price);
  };

  if (loading) return <div className="p-6 text-center">Cargando productos...</div>;
  if (error) return <div className="p-6 bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">{error}</div>;

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-orange-600 mb-6">Productos</h1>

      <div className="overflow-x-auto">
        <table className="w-full bg-white rounded-2xl shadow-xl overflow-hidden">
          <thead className="bg-orange-600 text-white">
            <tr>
              {['ID', 'Nombre', 'Descripción', 'Categoría', 'Precio', 'Unidad', 'Stock', 'Atributos', 'Acciones'].map((t, i) => (
                <th key={i} className="p-3 text-left">{t}</th>
              ))}
            </tr>
          </thead>

          <tbody>
            {productos.map(p => (
              <tr key={p.id} className="hover:bg-orange-50 transition-all duration-200 border-b">
                <td className="p-3">{p.id}</td>
                <td className="p-3 font-medium">{p.nombre || "-"}</td>
                <td className="p-3">
                  <div className="max-w-xs truncate" title={p.descripcion}>
                    {p.descripcion || "-"}
                  </div>
                </td>
                <td className="p-3">{p.categoria || "-"}</td>
                <td className="p-3 font-semibold">{p.precioActual ? formatPrice(p.precioActual) : "-"}</td>
                <td className="p-3">{p.unidadMedida || "-"}</td>
                <td className="p-3">{p.stock ?? "-"}</td>
                <td className="p-3">
                  {p.atributos?.length ? (
                    <div className="flex flex-wrap gap-1">
                      {p.atributos.slice(0,2).map((a,i) => (
                        <span key={i} className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded">{a}</span>
                      ))}
                      {p.atributos.length > 2 && <span className="text-xs text-gray-500">+{p.atributos.length-2}</span>}
                    </div>
                  ) : "-"}
                </td>
                <td className="p-3">
                  <button
                    className="text-orange-600 hover:text-orange-800 font-medium transition-colors duration-200"
                    onClick={() => setSelectedProducto(p)}
                  >
                    Ver detalles
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {selectedProducto && (
        <div 
          className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center z-50 animate-fade-in"
          onClick={() => setSelectedProducto(null)}
        >
          <div
            className="bg-white rounded-2xl p-6 max-w-2xl w-full mx-4 max-h-[90vh] overflow-y-auto shadow-xl animate-pop"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-2xl font-bold text-orange-600">{selectedProducto.nombre || "Producto"}</h2>
              <button
                onClick={() => setSelectedProducto(null)}
                className="text-gray-500 hover:text-gray-700 text-2xl transition-colors"
              >
                ×
              </button>
            </div>

            <div className="space-y-4">
              <div><label className="font-semibold text-gray-700">ID:</label> <p className="text-gray-900">{selectedProducto.id}</p></div>
              <div><label className="font-semibold text-gray-700">Nombre:</label> <p className="text-gray-900">{selectedProducto.nombre || "-"}</p></div>
              <div><label className="font-semibold text-gray-700">Descripción:</label> <p className="text-gray-900">{selectedProducto.descripcion || "-"}</p></div>

              <div className="grid grid-cols-2 gap-4">
                <div><label className="font-semibold text-gray-700">Precio:</label> <p className="text-gray-900">{selectedProducto.precioActual ? formatPrice(selectedProducto.precioActual) : "-"}</p></div>
                <div><label className="font-semibold text-gray-700">Unidad:</label> <p className="text-gray-900">{selectedProducto.unidadMedida || "-"}</p></div>
                <div><label className="font-semibold text-gray-700">Stock:</label> <p className="text-gray-900">{selectedProducto.stock ?? "-"}</p></div>
                <div><label className="font-semibold text-gray-700">Categoría:</label> <p className="text-gray-900">{selectedProducto.categoria || "-"}</p></div>
              </div>

              {selectedProducto.atributos?.length > 0 && (
                <div>
                  <label className="font-semibold text-gray-700">Atributos:</label>
                  <div className="flex flex-wrap gap-2 mt-1">
                    {selectedProducto.atributos.map((a,i) => (
                      <span key={i} className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm">{a}</span>
                    ))}
                  </div>
                </div>
              )}

              {selectedProducto.imagenes?.length > 0 && (
                <div>
                  <label className="font-semibold text-gray-700">Imágenes:</label>
                  <div className="grid grid-cols-3 gap-2 mt-1">
                    {selectedProducto.imagenes.map((img,i) => (
                      <img
                        key={i}
                        src={img}
                        alt={`${selectedProducto.nombre} ${i+1}`}
                        className="w-full h-24 object-cover rounded border"
                        onError={(e) => e.target.src = "https://via.placeholder.com/150?text=Imagen+no+disponible"}
                      />
                    ))}
                  </div>
                </div>
              )}

              <div><label className="font-semibold text-gray-700">ID Productor:</label> <p className="text-gray-900">{selectedProducto.productorId || "-"}</p></div>
            </div>

            <div className="mt-6 flex justify-end">
              <button
                onClick={() => setSelectedProducto(null)}
                className="bg-orange-600 text-white px-4 py-2 rounded-lg hover:bg-orange-700 transition-colors"
              >
                Cerrar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
