import { useEffect, useState } from "react";
import { client } from "../api/graphqlClient";
import { GET_VENTAS } from "../api/adminQueries";

export default function Ventas() {
  const [ventas, setVentas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchVentas = async () => {
      try {
        setLoading(true);
        setError("");
        const data = await client.request(GET_VENTAS);
        setVentas(data.ventas || []);
      } catch (err) {
        console.error("Error fetching ventas:", err);
        setError("Error al cargar ventas");
      } finally {
        setLoading(false);
      }
    };
    fetchVentas();
  }, []);

  if (loading) {
    return (
      <div className="p-6 text-center">
        <h1 className="text-3xl font-bold text-orange-600 mb-4">Ventas</h1>
        <div className="py-8">Cargando ventas...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6">
        <h1 className="text-3xl font-bold text-orange-600 mb-4">Ventas</h1>
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
          {error}
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-orange-600 mb-6">Ventas</h1>

      {ventas.length === 0 ? (
        <div className="text-center py-8 text-gray-500">No hay ventas registradas</div>
      ) : (
        <div className="overflow-x-auto">
          <table className="w-full bg-white rounded-2xl shadow-xl overflow-hidden">
            <thead className="bg-orange-600 text-white">
              <tr>
                {["ID", "Usuario", "Productor", "Monto Total", "Estado"].map((t,i) => (
                  <th key={i} className="p-3 text-left">{t}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {ventas.map(v => (
                <tr key={v.id} className="hover:bg-orange-50 transition-all duration-200 border-b">
                  <td className="p-3">{v.id}</td>
                  <td className="p-3">{v.usuarioId}</td>
                  <td className="p-3">{v.productorId}</td>
                  <td className="p-3 font-semibold">{v.montoTotal}</td>
                  <td className="p-3">{v.estado}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
