import { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { client } from "../api/graphqlClient";
import { LOGIN_MUTATION_VARIANTS, INTROSPECTION_QUERY } from "../api/adminQueries";

export default function Login() {
  const [correo, setCorreo] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    if (!correo.trim() || !password.trim()) {
      setError("Por favor, completa todos los campos");
      return;
    }

    setLoading(true);

    try {
      let data = null;
      let lastError = null;
      const variants = [
        { query: LOGIN_MUTATION_VARIANTS.variant1, variables: { correo, password } },
        { query: LOGIN_MUTATION_VARIANTS.variant2, variables: { correo, password } },
        { query: LOGIN_MUTATION_VARIANTS.variant3, variables: { input: { correo, password } } },
        { query: LOGIN_MUTATION_VARIANTS.variant4, variables: { correo, password } },
        { query: LOGIN_MUTATION_VARIANTS.variant5, variables: { correo, password } },
      ];

      for (const v of variants) {
        try {
          data = await client.request(v.query, v.variables);
          console.debug("GraphQL login response:", data);
          break;
        } catch (err) {
          lastError = err;
          console.warn("login variant failed", err);
          continue;
        }
      }

      if (!data) throw new Error("Error al iniciar sesión");

      const token =
        data.login ||
        data.Login ||
        data.authenticate ||
        data.signIn ||
        data.token;

      if (token) {
        login(token, []);
        navigate("/dashboard");
      } else {
        const serverMessage = lastError?.response?.errors?.[0]?.message;
        setError(serverMessage || "Credenciales inválidas");
      }
    } catch (err) {
      console.error("Login error:", err);
      const serverMessage = err?.response?.errors?.[0]?.message;
      setError(serverMessage || "Credenciales incorrectas");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-orange-100 to-orange-300">

      <div className="bg-white shadow-xl rounded-2xl p-10 w-96 border border-orange-200">
        <div className="flex justify-center mb-6">
        <img 
  src="/images/mercadea_logo.jpeg" 
  alt="Mercadea Logo" 
  className="h-12 w-auto"
/>

        </div>

        <h1 className="text-2xl text-center font-bold text-orange-600 mb-4">
          Panel Admin Mercadea
        </h1>

        <form onSubmit={handleSubmit}>
          <input
            className="border border-orange-300 focus:border-orange-500 focus:ring-orange-400 p-3 w-full rounded-lg mb-4 outline-none"
            placeholder="Correo"
            value={correo}
            onChange={(e) => setCorreo(e.target.value)}
          />

          <input
            className="border border-orange-300 focus:border-orange-500 focus:ring-orange-400 p-3 w-full rounded-lg mb-4 outline-none"
            placeholder="Contraseña"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          {error && (
            <div className="mb-3 p-2 bg-red-100 text-red-700 rounded text-sm text-center">
              {error}
            </div>
          )}

          <button
            type="submit"
            disabled={loading}
            className="bg-orange-500 hover:bg-orange-600 transition text-white w-full p-3 rounded-lg shadow-md font-semibold disabled:opacity-50"
          >
            {loading ? "Iniciando..." : "Ingresar"}
          </button>
        </form>
      </div>
    </div>
  );
}
