import { Outlet, Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

export default function DashboardLayout() {
  const { logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-900 text-white flex flex-col shadow-xl">
        {/* Logo */}
        <div className="flex items-center justify-center p-4 border-b border-gray-800">
          <img 
            src="/images/mercadea_logo.jpeg" 
            alt="Mercadea Logo" 
            className="h-12 w-auto rounded-full shadow-md"
          />
          <span className="ml-2 text-orange-500 font-bold text-lg">Admin</span>
        </div>

        {/* Navegación */}
        <nav className="flex-1 p-4">
          <ul className="space-y-2">
            {[
              { name: "Dashboard", path: "/dashboard" },
              { name: "Usuarios", path: "/usuarios" },
              { name: "Productores", path: "/productores" },
              { name: "Productos", path: "/productos" },
              { name: "Ventas", path: "/ventas" },
              { name: "Comprobadores", path: "/comprobadores" },
              { name: "Admin Forms", path: "/admin-forms" },
            ].map((link) => (
              <li key={link.path}>
                <Link 
                  to={link.path} 
                  className="block px-3 py-2 rounded hover:bg-orange-600 transition-colors"
                >
                  {link.name}
                </Link>
              </li>
            ))}
          </ul>
        </nav>

        {/* Botón de logout */}
        <button
          onClick={handleLogout}
          className="m-4 px-3 py-2 bg-orange-600 rounded hover:bg-orange-900 shadow-md transition-all"
        >
          Cerrar Sesión
        </button>
      </aside>

      {/* Contenido principal */}
      <main className="flex-1 p-6 bg-gray-100 overflow-auto">
        <Outlet />
      </main>
    </div>
  );
}
