import { useEffect, useState } from "react";
import { client } from "../api/graphqlClient";
import { GET_PRODUCTORES } from "../api/adminQueries";

export default function Productores() {
  const [productores, setProductores] = useState([]);

  useEffect(() => {
    const fetchProductores = async () => {
      try {
        const data = await client.request(GET_PRODUCTORES);
        setProductores(data.productores || []);
      } catch (error) {
        console.error("Error fetching productores:", error);
      }
    };
    fetchProductores();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold text-orange-600 mb-6">Productores</h1>

      <div className="overflow-x-auto">
        <table className="w-full bg-white rounded-2xl shadow-xl overflow-hidden">
          <thead className="bg-orange-600 text-white">
            <tr>
              {['ID', 'Nombre Usuario', 'Dirección', 'NIT'].map((t, i) => (
                <th key={i} className="p-3 text-left">{t}</th>
              ))}
            </tr>
          </thead>

          <tbody>
            {productores.map((p) => (
              <tr
                key={p.id}
                className="hover:bg-orange-50 transition-all duration-200 border-b"
              >
                <td className="p-3">{p.id}</td>
                <td className="p-3 font-medium">{p.nombreUsuario}</td>
                <td className="p-3">{p.direccion || "-"}</td>
                <td className="p-3">{p.nit || "-"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
